const http = require("https");

const options = {
  "method": "DELETE",
  "hostname": "api.openpix.com.br",
  "port": null,
  "path": "//api/openpix/v1/charge/%7Bid%7D",
  "headers": {
    "Authorization": "REPLACE_KEY_VALUE"
  }
};

