const http = require('https');

const options = {
  method: 'POST',
  hostname: '',
  port: null,
  path: '%3Cname%3E.indireto.woovi-indireto.dev/charge/pix-charge/v1/charge/%7Btxid%7D',
  headers: {
    'content-type': 'application/json'
  }
};

const req = http.request(options, function (res) {
  const chunks = [];

  res.on('data', function (chunk) {
    chunks.push(chunk);
  });

  res.on('end', function () {
    const body = Buffer.concat(chunks);
    console.log(body.toString());
  });
});

req.write(JSON.stringify({value: 0, key: 'string', keyType: 'CPF', expiresAt: '2019-08-24T14:15:22Z'}));
req.end();

OkHttpClient client = new OkHttpClient();

MediaType mediaType = MediaType.parse("application/json");
RequestBody body = RequestBody.create(mediaType, "{\"value\":0,\"key\":\"string\",\"keyType\":\"CPF\",\"expiresAt\":\"2019-08-24T14:15:22Z\"}");
Request request = new Request.Builder()
  .url("https:///%3Cname%3E.indireto.woovi-indireto.dev/charge/pix-charge/v1/charge/%7Btxid%7D")
  .post(body)
  .addHeader("content-type", "application/json")
  .build();

Response response = client.newCall(request).execute();

