const http = require('https');

const options = {
  method: 'POST',
  hostname: '',
  port: null,
  path: '%3Cname%3E.indireto.woovi-indireto.dev/pix-charge/v1/emv/',
  headers: {
    'content-type': 'application/json'
  }
};

const req = http.request(options, function (res) {
  const chunks = [];

  res.on('data', function (chunk) {
    chunks.push(chunk);
  });

  res.on('end', function () {
    const body = Buffer.concat(chunks);
    console.log(body.toString());
  });
});

req.write(JSON.stringify({
  merchantName: 'Test Merchant',
  merchantCity: 'Sao Paulo',
  merchantAccountInformationPix: {
    gui: 'br.gov.bcb.pix',
    url: 'https://example.com/pix',
    additionalInformation: 'Payment for services',
    fss: 'string',
    pixKey: 'string'
  },
  payloadFormatIndicator: '01',
  pointOfInitiationMethod: '12',
  merchantCategoryCode: '0000',
  transactionCurrency: '986',
  countryCode: 'BR',
  additionalDataFieldTemplate: {referenceLabel: '***'},
  unreservedTemplates: {
    gui: 'string',
    url: 'http://example.com',
    additionalInformation: 'string',
    fss: 'string',
    pixKey: 'string'
  }
}));
req.end();

OkHttpClient client = new OkHttpClient();

MediaType mediaType = MediaType.parse("application/json");
RequestBody body = RequestBody.create(mediaType, "{\"merchantName\":\"Test Merchant\",\"merchantCity\":\"Sao Paulo\",\"merchantAccountInformationPix\":{\"gui\":\"br.gov.bcb.pix\",\"url\":\"https://example.com/pix\",\"additionalInformation\":\"Payment for services\",\"fss\":\"string\",\"pixKey\":\"string\"},\"payloadFormatIndicator\":\"01\",\"pointOfInitiationMethod\":\"12\",\"merchantCategoryCode\":\"0000\",\"transactionCurrency\":\"986\",\"countryCode\":\"BR\",\"additionalDataFieldTemplate\":{\"referenceLabel\":\"***\"},\"unreservedTemplates\":{\"gui\":\"string\",\"url\":\"http://example.com\",\"additionalInformation\":\"string\",\"fss\":\"string\",\"pixKey\":\"string\"}}");
Request request = new Request.Builder()
  .url("https:///%3Cname%3E.indireto.woovi-indireto.dev/pix-charge/v1/emv/")
  .post(body)
  .addHeader("content-type", "application/json")
  .build();

Response response = client.newCall(request).execute();

